mapper.select:
select postNo, title, viewCount, author, content, thumbFile, registerDate
from board
where postNo = #{postNo};

mapper.update:
update board
set viewCount = viewCount + 1
where postNo = #{no};

update board
set title = #{title},
author = #{author},
content = #{content},
thumbFile = #{thumbFile}
where postNo = #{postNo}
and userId = #{userId};
